css:
@media print {
    @page {
        size: 2.1in 1in; 
        margin: 0;
    }

    #printarea {
        display: block;
        width: 2.1in; 
        height: 1in; 
    }

    .qr-container {
        width: 100%;
        height: 100%;
        display: flex;
        justify-content: center;
        align-items: center;
        page-break-inside: avoid;
    }

    .qr {
        width: 2.1in;
        height: 1in;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        text-align: center;
        position: relative;
        page-break-inside: avoid;
    }

    .qr-code {
        width: 0.7in;
        height: 0.7in;
    }

    .asset-code {
        width: auto;
        height: 0.3in;
        margin: 0;
        line-height: 0.3in;
    }
}
