css:
@media print {
    @page {
        size: 2.1in 1in;
        margin: 0;
    }

    #printarea {
        width: 2.1in;
        height: 1in;
        display: block; 
    }

    .qr-container {
        width: 100%;
        height: 100%;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .qr {
        width: 2.1in;
        height: 1in;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        text-align: center;
        position: relative;
    }

    .qr-code {
        width: 0.8in;
        height: 0.8in;
    }

    .asset-code {
        width: auto;
        height: 0.2in;
        margin: 0;
        line-height: 0.2in;
    }
}
