css:
@media print {
    @page {
        size: 2.1in 1in;
        margin: 0;
    }

    #printarea {
        width: 2.1in;
        height: 1in;
        display: block; 
    }

    .qr-container {
        width: 100%;
        height: 100%;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .qr {
        width: 2.1in;
        height: 1in;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        text-align: center;
        position: relative;
    }

    .qr-code {
        width: 0.7in;
        height: 0.7in;
    }

    .asset-code {
        width: auto;
        height: 0.3in;
        margin: 0;
        line-height: 0.3in;
    }
}

html:
{% set qr_code_query = frappe.db.sql("SELECT
                                            custom_qr_code_text
                                      FROM
                                            `tabAsset`
                                      WHERE
                                            name = %s
                                       ", (doc.name), as_dict = True)     
%}

<div id="printarea">
    {% for row in qr_code_query %}
    <div class="qr-container">
        <div class="qr">
            <img class="qr-code" src="{{ row.custom_qr_code_text }}">
            <h3 class="asset-code">{{ doc.name }}</h3>
            <h3 class="qr-code">{{ doc.name }}</h3>
        </div>
    </div>
    {% endfor %}
</div>


<style>

{% set site_url = frappe.utils.get_url() %}

@font-face {
	font-family: 'qr';
	font-style: normal;
	font-weight: 400;
	src: url('{{ site_url }}/assets/masar_assets/font/QR font tfb.ttf') format('truetype');
}

.qr-code {
	font-family: 'qr';
}

</style>
